        </div>

        {/* Technologies */}
        <div className="mb-16">
          <motion.h2
            className="text-center mb-8"
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            viewport={{ once: true }}
            className="text-slate-900"
